import React, { useEffect } from "react";
import './Graph.css';
import '../../jquery-loader';
import ReactFlot from 'react-flot';
import '../../../node_modules/react-flot/flot/jquery.flot.time.min';
import moment from 'moment';

function Graph(props) {

    let glucoseDBD = {}

    for (let i = 0; i < props.glucoseData.length; i++) {
        let reading = props.glucoseData[i]
        let dT = reading.result_dt_tm
        let day = dT.slice(0,10)
        if (glucoseDBD[day] == undefined) {
            glucoseDBD[day] = [reading]
        } else {
            glucoseDBD[day].push(reading)
        }
    }

    let glucoseDBDArr = []

    for (let i = 0; i < Object.keys(glucoseDBD).length; i++) {
        let date = Object.keys(glucoseDBD)[i]
        glucoseDBDArr.push({
            date: date,
            result: glucoseDBD[date]
        })
    }

    function createData(item) {
        let data = []

        for (let i = 0; i < props.glucoseRanges.length; i++) {
            console.log(props.glucoseRanges[i])
            let dataObj = {
                range: props.glucoseRanges[i].slice(0,2),
                data: [],
                color: props.glucoseRanges[i][2],
                series: {
                    lines: { 
                        show: true, 
                        fill: true, 
                        fillColor: {
                            colors: [{ opacity: 1 }, { opacity: 1 } ]
                        }
                    }
                }
            }
            data.push(dataObj)
        }

        console.log(data, item.result)

        for (let i = 0; i < item.result; i++) {
            for (let i = 0; i < data; i++) {
                let gL = item.result.glucose_level
                if (gL >= data[i].range[0] && gL <= data[i].range[1]) {
                    let time = parseInt(item.result.result_dt_tm.slice(11,13))
                    data[i].data.push([time, gL])
                }
            }
        }

        console.log(data)


        return (
            " "
        )
    }

    let ticks = [[0,"00:00"]]

    for (let i = 2; i < 25; i = i + 2) {
        if (i < 10) {
            ticks.push([i, `0${i}:00`])
        } else {
            ticks.push([i, `${i}:00`]) 
        }
    }

    const options = {
        xaxis: {
            min: 0, 
            max: 24, 
            mode: "time", 
            timeformat: "%H:%M",
            ticks: ticks
        }, 
        yaxis: {
            min: 0, 
            max: 350,
            ticks: [100, 180]
        },
        series: {
            lines: { show: true },
            points: { show: true }
        }
    }

    return (
        <>
            {
            props.selectedDates.start.length > 0 ?
                glucoseDBDArr.map((item) => {
                    console.log(item)
                    let momentS = moment(props.selectedDates.start).format('L')
                    let momentE = moment(props.selectedDates.end).format('L')
                    if (item.date >= momentS && item.date <= momentE) {
                        <ReactFlot id="product-chart" data={createData(item)} options={options} width="100%" height="250px" />

                    }
                })
            : " "
            }
        </>
      );

}

export default Graph;
