import React, { useEffect } from "react";
import './Graph.css';
import '../../jquery-loader';
import ReactFlot from 'react-flot';
import '../../../node_modules/react-flot/flot/jquery.flot.time.min';
import moment from 'moment';

function Graph(props) {

    let glucoseDBD = {}

    for (let i = 0; i < props.glucoseData.length; i++) {
        let reading = props.glucoseData[i]
        let dT = reading.result_dt_tm
        let day = dT.slice(0,10)
        if (glucoseDBD[day] == undefined) {
            glucoseDBD[day] = [reading]
        } else {
            glucoseDBD[day].push(reading)
        }
    }

    let glucoseDBDArr = []

    for (let i = 0; i < Object.keys(glucoseDBD).length; i++) {
        let date = Object.keys(glucoseDBD)[i]
        glucoseDBDArr.push({
            date: date,
            result: glucoseDBD[date]
        })
    }

    glucoseDBDArr.reverse()

    function createData(item) {
        let data = []

        for (let i = 0; i < props.glucoseRanges.length; i++) {
            let dataObj = {
                range: props.glucoseRanges[i].slice(0,2),
                data: [],
                color: props.glucoseRanges[i][2],
                series: {
                    lines: { 
                        show: true, 
                        fill: true, 
                        fillColor: {
                            colors: [{ opacity: 1 }, { opacity: 1 } ]
                        }
                    }
                }
            }
            data.push(dataObj)
        }

        for (let i0 = 0; i0 < item.result.length; i0++) {
            for (let i1 = 0; i1 < data.length; i1++) {
                let gL = item.result[i0].glucose_level
                if (gL >= data[i1].range[0] && gL <= data[i1].range[1]) {
                    let time = parseInt(item.result[i0].result_dt_tm.slice(11,13))
                    data[i1].data.push([time, gL])
                }
            }
        }

        let TandGL = []

        for (let i = 0; i< data.length; i++) {
            if (data[i].data.length != undefined) {
                data[i].data.forEach((arr) => {
                    TandGL.push(arr)
                })
            }
        }

        TandGL.sort((a, b) => {
            return a[0] - b[0]
        })

        let thresholds = []

        for (let i = 0; i < props.glucoseRanges.length; i++) {
            thresholds.push(props.glucoseRanges[i][0])
            thresholds.push(props.glucoseRanges[i][1])
        }

        console.log(thresholds, TandGL)

        let seriesArr = []

        for (let i0 = 0; i0 < TandGL.length - 1; i0++) {
            if (TandGL.length > 0 && TandGL[i0][1] < TandGL[i0+1][1]) {
                let thresholdPasses = []
                for (let i1 = 0; i1 < thresholds.length; i1++) {
                    if (TandGL[i0][1] < thresholds[i1] && 
                        TandGL[i0+1][1] > thresholds[i1]) {
                        thresholdPasses.push(thresholds[i1])
                    }
                }
                let currentSeries = [TandGL[i0]]
                let totalTimeGap = TandGL[i0+1][0] - TandGL[i0][0]
                let totalPointsGap = TandGL[i0+1][1] - TandGL[i0][1]
                for (let i2 = 0; i2 < thresholdPasses.length; i2++) {
                    let pointsDif = thresholdPasses[i2] - TandGL[i0][1]
                    let percOfTot = pointsDif/totalPointsGap
                    let timeToAdd = percOfTot * totalTimeGap 
                    let time = timeToAdd + TandGL[i0][0]
                    currentSeries.push([time, thresholdPasses[i2]])
                    if (i2 == thresholdPasses.length - 1 ) {
                        currentSeries.push(TandGL[i0+1])
                    }
                    if (currentSeries.length == 2) {
                        seriesArr.push(currentSeries)
                        currentSeries = []
                    }
                }
                if (thresholdPasses.length == 0) {
                    currentSeries.push(TandGL[i0+1])
                }
            } else if (TandGL.length > 0 && TandGL[i0][1] > TandGL[i0+1][1]) {
                let thresholdPasses = []
                for (let i1 = 0; i1 < thresholds.length; i1++) {
                    if (TandGL[i0+1][1] < thresholds[i1] && 
                        TandGL[i0][1] > thresholds[i1]) {
                        thresholdPasses.push(thresholds[i1])
                    }
                }
                thresholdPasses.reverse()
                let currentSeries = [TandGL[i0]]
                let totalTimeGap = TandGL[i0][0] - TandGL[i0+1][0]
                let totalPointsGap = TandGL[i0+1][1] - TandGL[i0][1]
                for (let i2 = 0; i2 < thresholdPasses.length; i2++) {
                    let pointsDif = TandGL[i0][1] - thresholdPasses[i2]
                    let percOfTot = pointsDif/totalPointsGap
                    let timeToAdd = percOfTot * totalTimeGap 
                    let time = timeToAdd + TandGL[i0][0]
                    currentSeries.push([time, thresholdPasses[i2]])
                    if (i2 == thresholdPasses.length - 1 ) {
                        currentSeries.push(TandGL[i0+1])
                        currentSeries.push(TandGL[i0+1])
                    }
                    if (currentSeries.length == 2) {
                        seriesArr.push(currentSeries)
                        currentSeries = []
                    }
                }
                if (thresholdPasses.length == 0) {
                    currentSeries.push(TandGL[i0+1])
                    seriesArr.push(currentSeries)
                }
            }
        }

        console.log(seriesArr)

        let dataToSend = []

        for (let i0 = 0; i0 < seriesArr.length; i0++) {
            let color
            for (let i1 = 0; i1 < props.glucoseRanges; i1++) {
                if (seriesArr[i0][0][1] > props.glucoseRanges[i1][0] &&
                    seriesArr[i0][0][1] < props.glucoseRanges[i1][1]) {
                    color = props.glucoseRanges[i1][2]
                }
            }
            dataToSend.push({
                data: seriesArr[i0],
                color: color,
                series: {
                    lines: {
                        lineWidth: 20,
                        show: true, 
                        fill: true, 
                        fillColor: {
                            colors: [{ opacity: 1 }, { opacity: 1 } ]
                        }
                    }
                } 
            })
        }

        return dataToSend
    }

    let ticks = [[0,"00:00"]]


    for (let i = 2; i < 25; i = i + 2) {
        if (i < 10) {
            ticks.push([i, `0${i}:00`])
        } else {
            ticks.push([i, `${i}:00`]) 
        }
    }

    if (window.innerWidth < 725) {
        ticks = [[0,"00"]]
        for (let i = 2; i < 25; i = i + 2) {
            if (i < 10) {
                ticks.push([i, `0${i}`])
            } else {
                ticks.push([i, `${i}`]) 
            }
        }
    }

    const options = {
        xaxis: {
            min: 0, 
            max: 24, 
            mode: "time", 
            timeformat: "%H:%M",
            ticks: ticks
        }, 
        yaxis: {
            min: 0, 
            max: 700,
            ticks: [100, 180]
        },
        series: {
            lines: { show: true }
            // points: { show: true }
        }
    }

    return (
        <>
            {
            props.selectedDates.start.length > 0 ?
                glucoseDBDArr.map((item) => {
                    let momentS = moment(props.selectedDates.start).format('L')
                    let momentE = moment(props.selectedDates.end).format('L')
                    if (item.date >= momentS && item.date <= momentE) {
                        let addClass = ""
                        if (momentS == item.date || momentS == momentE) {
                            addClass = "lastChart"
                        }
                        return (
                            <div className={`ChartContainer ${addClass}`}>
                                <h5 className="ChartDate">
                                    {item.date}
                                </h5>
                                <ReactFlot 
                                key={`key${item.date.replaceAll('/', '')}`}
                                id={`${item.date.replaceAll('/', '')}`} 
                                className="FlotChart"
                                data={createData(item)} 
                                options={options} 
                                width="100%" 
                                height="250px" 
                            />
                            </div>
                        )

                    }
                })
            : " "
            }
        </>
      );

}

export default Graph;
