import React, { useEffect, useState } from "react";
import './App.css';
import axios from "axios";
import Nav from "./components/Nav";
import Graph from "./components/Graph";


function App() {

  const [glucoseData, setGlucoseData] = useState([])
  const [glucoseRanges, setGlucoseRanges] = useState([])
  const [selectedDates, setSelectedDates] = useState({
    start: "",
    end: ""
  })

  useEffect(()=> {
    axios.get(`https://caiken.dev.transformativemed.com/david-skills-test/data.php`)
      .then(res => {
        setGlucoseData(res.data.glucose_data)
        setGlucoseRanges(res.data.glucose_ranges)
        let startDT = glucoseData[0].result_dt_tm
        let startD = startDT.slice(0, startDT.search(" "))
        let endDT = glucoseData[glucoseData.length - 1].result_dt_tm
        let endD = endDT.slice(0, endDT.search(" "))
        setSelectedDates({
          start: startD,
          end: endD
        })
      })
  }, [])

  return (
    <div className="App">
      <Nav props={selectedDates, setSelectedDates}/>
      <Graph props={selectedDates, glucoseData, glucoseRanges}/>
    </div>
  );
}

export default App;
