import React, { useEffect, useState } from "react";
import './App.css';
import axios from "axios";
import Nav from "./components/Nav";
import Graph from "./components/Graph";


function App() {

  const [glucoseData, setGlucoseData] = useState([])
  const [glucoseRanges, setGlucoseRanges] = useState([])
  const [selectedDates, setSelectedDates] = useState({
    start: "",
    end: ""
  })

  useEffect(()=> {
    axios.get(`https://caiken.dev.transformativemed.com/david-skills-test/data.php`)
      .then(res => {
        let data = res.data
        setGlucoseData(data.glucose_data)
        setGlucoseRanges(data.glucose_ranges)
        let startDT = data.glucose_data[0].result_dt_tm
        let startD = startDT.slice(0, startDT.search(" "))
        let endDT = data.glucose_data[data.glucose_data.length - 1].result_dt_tm
        let endD = endDT.slice(0, endDT.search(" "))
        setSelectedDates({
          start: startD,
          end: endD
        })
      })
  }, [])

  return (
    <div className="App">
      <Nav selectedDates={selectedDates} setSelectedDates={setSelectedDates}/>
      <Graph selectedDates={selectedDates} glucosData={glucoseData} glucoseRanges={glucoseRanges}/>
    </div>
  );
}

export default App;
